import React, { useState, useEffect } from 'react';
import { useHistory, useParams } from 'react-router-dom';
import { Table, Button, Pagination } from 'react-bootstrap';
import _ from 'lodash';
import api from '../../services/api';

import moment from 'moment';
import axios from 'axios';

interface IProduto {
  id: number;
  name: string;
  price: number;
  created_at: Date;
  updated_at: Date;
}

const Produtos: React.FC = () => {
  const history = useHistory();
  const [produtos, setProdutos] = useState<IProduto[]>([]);
  const [paginatedProdutos, setPaginatedProdutos] = useState();
  const [currentPage, setCurrentPage] = useState(1);
  const pageSize = 5;

  useEffect(() => {
    axios.get('/produtos').then((response) => {
      setProdutos(response.data);
      console.log(response.data);
      setPaginatedProdutos(_(response.data).slice(0).take(pageSize).value());
    });
  }, []);

  //Função que apaga os produtos da api
  async function removeProduto(id: number) {
    await api.delete(`/produtos/${id}`);
    loadProdutos();
  }

  // Formatação da data
  function formateDate(date: Date) {
    return moment(date).format('DD/MM/YYYY');
  }

  // Formatação do preço
  function formatePrice(price: Number) {
    return price.toLocaleString('pt-br', {
      style: 'currency',
      currency: 'BRL',
    });
  }

  //Função que edita os produtos da api
  function editProduto(id: number) {
    history.push(`/produtos_cadastro/${id}`);
  }

  //Função que direciona para a info do produto com id
  function viewProduto(id: number) {
    history.push(`/produtos/${id}`);
  }

  // Troca page
  function paginate(pageNumber: any) {
    console.log(pageNumber);
    // return setCurrentPage(pageNumber);
  }

  const pageCount = produtos ? Math.ceil(produtos.length / pageSize) : 0;
  if (pageCount === 1) return null;
  const pages = _.range(1, pageCount + 1);
  return (
    <>
      <div className="text-center">
        <br />
        <h1> Lista de produtos </h1>
        <br />

        <Table striped bordered hover variant="dark" className="text-center">
          <thead>
            <tr>
              <th>ID</th>
              <th>Nome</th>
              <th>Preço</th>
              <th>Data de criação</th>
              <th>Última atualização</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {produtos.map((produto) => (
              <tr>
                <td>{produto.id}</td>
                <td>{produto.name}</td>
                <td>{formatePrice(produto.price)}</td>
                <td>{formateDate(produto.created_at)}</td>
                <td>{formateDate(produto.updated_at)}</td>
                <td>
                  <Button
                    size="sm"
                    onClick={() => viewProduto(produto.id)}
                    variant="info"
                  >
                    Ver
                  </Button>{' '}
                  <Button size="sm" onClick={() => editProduto(produto.id)}>
                    Editar
                  </Button>{' '}
                  <Button
                    size="sm"
                    variant="danger"
                    onClick={() => removeProduto(produto.id)}
                  >
                    Apagar
                  </Button>{' '}
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
        <ul>
          {pages.map((page) => (
            <li
              className={
                page === currentPage ? 'page-item active' : 'page-item'
              }
            >
              <p className="page-link">{page}</p>
            </li>
          ))}
        </ul>
        <br />
      </div>
    </>
  );
};

export default Produtos;
