import React from 'react';
import { Pagination } from 'react-bootstrap';
import Produtos from '..';

const PaginationProp: React.FC = () => {
  const produtosperPage = 10;
  const totalProdutos;
  const PageNum = [];
  let active = currentPage;
  for (
    let index = 1;
    index <= Math.ceil(totalProdutos / produtosperPage);
    index++
  ) {
    PageNum.push(
      <Pagination.Item key={index} active={index === active}>
        {index}
      </Pagination.Item>
    );
  }

  return (
    <div>
      <Pagination>{PageNum}</Pagination>
      <br />
    </div>
  );
};

export default PaginationProp;
