import React, { useState, useEffect } from 'react';
import { Table, Pagination } from 'react-bootstrap';
import api from '../../../services/api';

import moment from 'moment';

interface IPagination {
  id: number;
  name: string;
  price: number;
  created_at: Date;
  updated_at: Date;
  loading?: boolean;
  currentPage: number;
  produtoPerPage: number;
}

const PaginationProp: React.FC<IPagination> = () => {
  const [produtos, setProdutos] = useState([]);
  const [loading, setLoading] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [produtoPerPage, setProdutoPerPage] = useState(10);

  useEffect(() => {
    loadProdutos();
  }, []);

  // Função que carrega os produtos da api
  async function loadProdutos() {
    setLoading(true);
    const response = await api.get('/produtos');
    setProdutos(response.data);
    console.log(response.data);
    setLoading(false);
  }

  const indexOfLastProduto = currentPage * produtoPerPage;
  const indexOfFirstProduto = indexOfLastProduto - produtoPerPage;
  // const currentPage = produtos.slice(indexOfFirstProduto, indexOfLastProduto);

  let active = currentPage;
  let items = [];
  for (let number = 1; number <= 5; number++) {
    items.push(
      <Pagination.Item key={number} active={number === active}>
        {number}
      </Pagination.Item>
    );
  }

  // Formatação da data
  function formateDate(date: Date) {
    return moment(date).format('DD/MM/YYYY');
  }

  // Formatação do preço
  function formatePrice(price: Number) {
    return price.toLocaleString('pt-br', {
      style: 'currency',
      currency: 'BRL',
    });
  }

  return (
    <div className="text-center">
      <br />
      <h1> Lista de produtos </h1>
      <br />

      <Table striped bordered hover variant="dark" className="text-center">
        <thead>
          <tr>
            <th>ID</th>
            <th>Nome</th>
            <th>Preço</th>
            <th>Data de criação</th>
            <th>Última atualização</th>
          </tr>
        </thead>
        <tbody>
          {/* {produtos.map((produto) => (
            <tr>
              <td>{produto.id}</td>
              <td>{produto.name}</td>
              <td>{formatePrice(produto.price)}</td>
              <td>{formateDate(produto.created_at)}</td>
              <td>{formateDate(produto.updated_at)}</td>
            </tr> */}
          ))}
        </tbody>
      </Table>

      <Pagination>{items}</Pagination>
      <br />
    </div>
  );
};

export default PaginationProp;
