import React, {useState, useEffect} from "react";
import {Badge, Table, Button} from "react-bootstrap";
import { getAllJSDocTagsOfKind } from "typescript";
import api from "../../services/api";

import moment from "moment";

interface IProduto {
    id: number;
    name: string;
    price: number;
    created_at: Date;
    updated_at: Date;
}

const Produtos: React.FC = () => {

    const [produtos, setProdutos] = useState<IProduto[]>([])

    useEffect(() => {
        loadProdutos()
    }, [])

    async function loadProdutos(){

        const response = await api.get('/produtos')
        console.log(response)
        setProdutos(response.data)
    }

    function formateDate(date: Date){
        return moment(date).format("DD/MM/YYYY")
    }

    return(
        <><div className="container" className="text-center">
            <br />
            <h1> Lista de produtos </h1>
            <br />
        </div><Table striped bordered hover variant="dark" className="text-center">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Nome</th>
                        <th>Preço</th>
                        <th>Data de criação</th>
                        <th>Última atualização</th>
                        <th>Ações</th>
                    </tr>
                </thead>
                <tbody>

                    {
                    
                    produtos.map(produto => (
                        <tr>
                            <td>{ produto.id }</td>
                            <td>{ produto.name }</td>
                            <td>{ produto.price }</td>
                            <td>{ formateDate(produto.created_at) }</td>
                            <td>{ formateDate(produto.updated_at) }</td>
                            <td>
                                <Button size="sm">Editar</Button>{' '}
                                <Button size="sm" variant="info">Vizualizar</Button>{' '}
                                <Button size="sm" variant="danger">Apagar</Button>{' '}
                            </td>
                        </tr>
                    ))
                    
                    }

                </tbody>
            </Table></>
    )
}

export default Produtos;
