import React, {useState, useEffect, ChangeEvent} from "react";
import { useParams } from "react-router-dom";
import { Form, Button } from "react-bootstrap";
import api from "../../../services/api";



interface IProduto {
    name: string;
    price: number;
}

const Forms: React.FC = () => {

    const params = useParams()
    const [model, setModel] = useState<IProduto>({
        name: '',
        price: 0
    })

    useEffect(() => {
        console.log(params)
    }, [params])

    function updatedModel (e: ChangeEvent<HTMLInputElement>): void{

        setModel({
            ...model,
            [e.target.name]: e.target.value
        })
    }

    async function onSubmit (e: ChangeEvent<HTMLFormElement>) {
        e.preventDefault()

        const response = await api.post('/produtos', model)

        console.log(response)

    }

    async function findProduto(params: string){
        const response = await api.get(`produtos/${params}`)
        console.log(response)
    }

    return(
        <><div className="text-center">
            <br />
            <h1> Lista de produtos </h1>
            <br />
        </div>
        <div className="container">
            <Form onSubmit={onSubmit}>
                <Form.Group>
                    <Form.Label>Nome</Form.Label>
                    <Form.Control type="text" name="name" 
                    onChange={(e: ChangeEvent<HTMLInputElement>) => updatedModel(e)}
                    />
                </Form.Group>

                <Form.Group>
                    <Form.Label>Pre√ßo</Form.Label>
                    <Form.Control type="number" name="price" 
                    onChange={(e: ChangeEvent<HTMLInputElement>) => updatedModel(e)}
                    />
                </Form.Group>
                <br />
                <Button variant="success" type="submit"active>
                    Enviar
                </Button>
            </Form>
        </div></>
    )
}

export default Forms;
